plt.rcParams.update({'font.size': 50})
plt.subplots_adjust(hspace=10.0)
fig, ax = plt.subplots(12, 1,figsize=(100,500));

for col, axe in zip(df_v_type.columns[2: 14], ax.ravel()):
    df = df_v_type[["Location",col]]
    top_df = df.groupby("Location").agg(['sum'])[col]['sum'].sort_values(ascending=False)[:10]
    districts = [ x.split(" ")[0] for x in top_df.index]
    sns.barplot( x = districts, y = top_df.values, ax=axe)
    axe.set_xticklabels(labels=districts )
    axe.set_ylabel("Number of Accidents")
    axe.set_xlabel("District")
    axe.set_title(f"Top 10 District by {col} Accidents from 2010 - 2012");


plt.show();

