def sum_relationship(a,b):
    df = copy_dataset.groupby(a)[b].sum()
    df = df.reset_index()
    df[b] = (df[b]*100)/sum(copy_dataset[b])
    df = df.sort_values(by=[b])
    return df.plot.barh(x=a,y=b, figsize = (12,20))

