rare_map = {}
for i,t in train.iterrows():
    fs = []
    for w in word_rex.findall(t.text):
        fs.append((w, wordlist[w]))
    if len(fs) == 0:
        continue
    fs.sort(key=lambda x:x[1])
    for rare_w,_ in fs[:3]:
        if rare_w not in rare_map:
            rare_map[rare_w] = [t]
        else:
            rare_map[rare_w].append(t)
print("Built hash table")

