def find(pt):
    fs = []
    for w in word_rex.findall(pt):
        if w in rare_map.keys():
            fs.append((w, wordlist[w]))
    if len(fs)==0:
        return None
    fs.sort(key=lambda x:x[1])
    for rare_w, _ in fs[:5]: #We'll check up to 5 rare words, just to be safe.
        for t in rare_map[rare_w]:
            if t.text in pt:
                return t

