#To change the age values in age column to a categorical values
l=[]
for i,j in ins_df.iteritems():
    if(i=='age'):
        for m in range(len(j)):
            if(j[m]<=35):
                l.append('Young Adult')
            elif((j[m]>=36)&(j[m]<=55)):
                l.append('Senior Adult')
            elif(j[m]>=56):
                l.append('Elder')
