# creating sentiment using Textblob

def data_senti(text):
    TB_sentiment_polarity = TextBlob(text).sentiment.polarity

    # decide sentiment as positive, negative and neutral 
    if TB_sentiment_polarity >= 0.00 : 
        return "Positive" 

    elif TB_sentiment_polarity <= 0.00 : 
        return "Negative" 

    else : 
        return "Neutral"
