# Dictionary of Blood types
blood_groups = {
    'A': 30,
    'B': 24,
    'AB': 8,
    'O': 38
};

# Extracting data from the dictionary
blood_type = list(blood_groups.keys()); # Blood types
blood_no = list(blood_groups.values()); # Number of people classified by blood types

source.0eb1d5b2:
# Creating a figure for the Bar-chart
fig_bar = plt.figure();
ax_bar = fig_bar.add_axes([0,0,1,1]);

# Designing the Bar-chart
blood_bar = ax_bar.bar(
    x=blood_type, 
    height=blood_no,
    color=['#93ecff', '#63b3ce', '#367e9d', '#004c6d'],
    edgecolor=['#5abaf4', '#3e93c5', '#226f98', '#004c6d']
);

# Labeling the Bar-chart
ax_bar.set_title('Number of blood types - Bar Chart');
ax_bar.set_xlabel('Blood types');
ax_bar.set_ylabel('Number of people');

source.c6ac5970:
# Creating a figure for the Pie-chart
fig_pie, ax_pie = plt.subplots(figsize=(6, 3));

wedges, texts, autotext = ax_pie.pie(
    blood_no,
    autopct=(lambda pct:"{:.0f}%".format(pct)),
    textprops=dict(color="w"),
    colors=['#5abaf4', '#3e93c5', '#226f98', '#004c6d']
);

ax_pie.legend(
    wedges,
    blood_type,
    title="Blood types",
    loc=6,
    bbox_to_anchor=(1, 0, 0.5, 1)
);

ax_pie.set_title('Number of blood types - Pie Chart');

