def value_change(row): #converting everything in terms of 'thousands of euros'
    if row[-1] == 'M':
        row = row[1:-1] # getting rid of euro and million sign
        num = pd.to_numeric(row)
        num = num * 1000
        return num
    else:
        return pd.to_numeric(row[1:-1])

