v = [None]*n
for i in range(n):
    v[i] = int(input())
moda = v[0]
qtde = 0
for i in range(n):
    aux = 0
    for j in range(n):
         if (v[j]!= None and v[i] == v[j]):
            aux = aux + 1
            moda = v[i]
    if (aux >= qtde):
        moda = v[i]
        qtde = aux
