top_features = ['Rate','Metascore','Gross_Worldwide','Profit_in_%']
films = {}
for i in top_features:
    for h, j in enumerate(df.sort_values(by=i, ascending=False).index):
        if j in films:
            films[j] += h
        else:
            films[j] = h

df['Score'] = df.index.map(films)

