dates['season'] = np.empty(shape= dates.shape[0])

i = 0
for m in dates['month']:
    season = None
    if m in ['November', 'December', 'January', 'February']:
        season = 'winter'
    elif m in ['March', 'April', 'May']:
        season = 'summer'
    elif m in ['June', 'July', 'August', 'September', 'October']:
        season = 'monsoon'

    dates['season'][i] = season
    i += 1

dates.head()
