    d[c]['TR']=d[c].iloc[:,2]-d[c].iloc[:,3]
    d[c]['H_O']=d[c].iloc[:,2]-d[c].iloc[:,1]
    d[c]['O_L']=d[c].iloc[:,1]-d[c].iloc[:,3]
