    d[c]['OL']=d[c].iloc[:,7]/d[c].iloc[:,5]
    WHA_HO=[0,0,0,0,0]
    for i in range (0,len(d[c])-5):
        x=(1*d[c].iloc[i,8]+2*d[c].iloc[i+1,8]+3*d[c].iloc[i+2,8]+4*d[c].iloc[i+3,8]+5*d[c].iloc[i+4,8])/(1+2+3+4+5)
        WHA_HO.append(x)
    d[c]['WMA_HO']=WHA_HO

    WHA_OL=[0,0,0,0,0]
    for i in range (0,len(d[c])-5):
        x=(1*d[c].iloc[i,9]+2*d[c].iloc[i+1,9]+3*d[c].iloc[i+2,9]+4*d[c].iloc[i+3,9]+5*d[c].iloc[i+4,9])/(1+2+3+4+5)
        WHA_OL.append(x)
    d[c]['WMA_OL']=WHA_OL

    Expected_TR=[0,0,0,0,0]
    for i in range (0,len(d[c])-5):
        x=max(d[c].iloc[i:i+5,5])
        Expected_TR.append(x)
    d[c]['Expected_TR']=Expected_TR

    d[c]['Proj_H']=d[c].iloc[:,1]+d[c].iloc[:,10]*d[c].iloc[:,12]
    d[c].at[0:4,'Proj_H']=0

    d[c]['Proj_L']=d[c].iloc[:,1]-d[c].iloc[:,11]*d[c].iloc[:,12]
    d[c].at[0:4,'Proj_L']=0

    SDHO=[0,0,0,0,0]
    for i in range (0,len(d[c])-5):
        x=stdev(d[c].iloc[i:i+5,6])
        SDHO.append(x)
    d[c]['SDHO']=SDHO

    SDOL=[0,0,0,0,0]
    for i in range (0,len(d[c])-5):
        x=stdev(d[c].iloc[i:i+5,7])
        SDOL.append(x)
    d[c]['SDOL']=SDOL

    d[c]['STPT_PH']=d[c].iloc[:,13]+(NUM_SD*d[c].iloc[:,15])
    d[c]['STPT_PL']=d[c].iloc[:,14]-(NUM_SD*d[c].iloc[:,16])

    buy_trade=[0,0,0,0,0]
    for i in range (0,len(d[c])-5):
        if d[c].iloc[i+5,2]>d[c].iloc[i+5,13]:
            buy_trade.append('Buy')
        else:
            buy_trade.append(0)
    d[c]['buy_trade']=buy_trade

    sell_trade=[0,0,0,0,0]
    for i in range (0,len(d[c])-5):
        if d[c].iloc[i+5,3]<d[c].iloc[i+5,14]:
            sell_trade.append('Sell')
        else:
            sell_trade.append(0)
    d[c]['sell_trade']=sell_trade

    ## below is the enter buy, exit buy and daily return buy

    Enter_B=[0,0,0,0,0]
    for i in range (0,len(d[c])-5):
        if d[c].iloc[i+5,19]=='Buy':
            Enter_B.append(d[c].iloc[i+5,13])
        else:
            Enter_B.append(0)
    d[c]['Enter_B']=Enter_B

    Exit_B=[0,0,0,0,0]
    for i in range (0,len(d[c])-5):
        if d[c].iloc[i+5,19]=='Buy'and d[c].iloc[i+5,2]>d[c].iloc[i+5,17]:
            Exit_B.append(d[c].iloc[i+5,17])
        else:
            Exit_B.append(d[c].iloc[i+5,4])
    d[c]['Exit_B']=Exit_B

    D_return_B=[0,0,0,0,0]
    for i in range (0,len(d[c])-5):
        if d[c].iloc[i+5,21]==0:
            D_return_B.append(0)
        else:
            D_return_B.append((d[c].iloc[i+5,22]-d[c].iloc[i+5,21])/d[c].iloc[i+5,4])
    d[c]['D_return_B']=D_return_B

    ## below is the enter sell, exit sell and daily return sell
    Enter_S=[0,0,0,0,0]
    for i in range (0,len(d[c])-5):
        if d[c].iloc[i+5,20]=='Sell':
            Enter_S.append(d[c].iloc[i+5,14])
        else:
            Enter_S.append(0)
    d[c]['Enter_S']=Enter_S

    Exit_S=[0,0,0,0,0]
    for i in range (0,len(d[c])-5):
        if d[c].iloc[i+5,20]=='Sell'and d[c].iloc[i+5,3]<d[c].iloc[i+5,18]:
            Exit_S.append(d[c].iloc[i+5,18])
        else:
            Exit_S.append(d[c].iloc[i+5,4])
    d[c]['Exit_S']=Exit_S

    D_return_S=[0,0,0,0,0]
    for i in range (0,len(d[c])-5):
        if d[c].iloc[i+5,24]==0:
            D_return_S.append(0)
        else:
            D_return_S.append((d[c].iloc[i+5,24]-d[c].iloc[i+5,25])/d[c].iloc[i+5,4])
    d[c]['D_return_S']=D_return_S

    d[c]['D_return_buy&sell']=d[c].iloc[:,23]+d[c].iloc[:,26]

    cumulative_D_return=[0,0,0,0,0]
    for i in range (0,len(d[c])-5):
        x=cumulative_D_return[i-1+5]+d[c].iloc[i+5,27]
