    d[c]['cumulative_D_return']=cumulative_D_return

    HWM=[0,0,0,0,0]
    for i in range (0,len(d[c])-5):
        x=max(HWM[i-1+5],d[c].iloc[i+5,28])
        HWM.append(x)
    d[c]['HWM']=HWM

    DD=[]
    for i in range(0,len(d[c])):
        if d[c].iloc[i,28]<d[c].iloc[i,29]:
            DD.append(d[c].iloc[i,28]-d[c].iloc[i,29])
        else:
            DD.append(0)
    d[c]['DD']=DD

    ## below is the DD and rebound control
    Recent_low=[0,0,0,0,0]
    for i in range (0,len(d[c])-5):
        if d[c].iloc[i+5,30]==0:
            Recent_low.append(0)
        else:
            Recent_low.append(min(d[c].iloc[i+5,30],Recent_low[i+5-1]))
    d[c]['Recent_low']=Recent_low

    DDC_index_list=[0,0,0,0,0]
    for i in range (0,len(d[c])-5):
        if d[c].iloc[i+5-1,30]>DDC_index:
            DDC_index_list.append(1)
        else:
            DDC_index_list.append(0)
    d[c]['DDC_index']=DDC_index_list

    Rebound_list=[0,0,0,0,0]
    for i in range (0,len(d[c])-5):
