            Rebound_list.append(1)
        else:
            Rebound_list.append(0)
    d[c]['Rebound']=Rebound_list

    DR_DDC=[]
    for i in range(0,len(d[c])):
        DR_DDC.append(d[c].iloc[i,27]*(max(d[c].iloc[i,32],d[c].iloc[i,33])))
    d[c]['DR_DDC']=DR_DDC

    CR_DDC=[0,0,0,0,0]
    for i in range(0,len(d[c])-5):
        CR_DDC.append(CR_DDC[i+5-1]+d[c].iloc[i+5,34])
    d[c]['CR_DDC']=CR_DDC

    HWM_DDC=[0,0,0,0,0]
    for i in range (0,len(d[c])-5):
        x=max(HWM_DDC[i-1+5],d[c].iloc[i+5,35])
        HWM_DDC.append(x)
    d[c]['HWM_DDC']=HWM_DDC

    DD_DDC=[]
    for i in range(0,len(d[c])):
        if d[c].iloc[i,35]<d[c].iloc[i,36]:
            DD_DDC.append(d[c].iloc[i,35]-d[c].iloc[i,36])
        else:
            DD_DDC.append(0)
    d[c]['DD_DDC']=DD_DDC


    SD_daily=stdev(d[c].iloc[:,27])
    Return_daily=mean(d[c].iloc[:,27])
    annual_return=Return_daily*260
    annual_SD=SD_daily*(260**(1/2))
    HWM_no_DDC=max(d[c].iloc[:,29])
    MDD_no_DDC=min(d[c].iloc[:,30])
    RR_ratio=annual_return/annual_SD
