class Animal:
    def __init__(self,nb_pattes,nb_yeux,age):
        self.nb_pattes=nb_pattes
        self.nb_yeux=nb_yeux
        self.age=age
    def annee_de_naissance(self):
        return 2019-self.age

class AnimalDeCompagnie(Animal):
    def __init__(self,nb_pattes,nb_yeux,age,proprietaire):
        super().__init__(nb_pattes,nb_yeux,age)
        self.proprietaire=proprietaire

