def load_best_model():
    tmp_file_name = os.listdir('.')
    acc_ind = {}
    acc_list = []
    for i, file in enumerate(tmp_file_name):
        if file[:5] == "weigh" :
            acc = int((file.split('.')[1]).split('-')[0])
            acc_list.append(acc)
            acc_ind[acc] = i
    max_acc = max(acc_list)
    print(tmp_file_name[acc_ind[max_acc]])
    return tmp_file_name[acc_ind[max_acc]]
