# 2. 간단한 함수로 만들어 apply 메소드 활용
def category_age(x):
    if x < 10:
        return 0
    elif x < 20:
        return 1
    elif x < 30:
        return 2
    elif x < 40:
        return 3
    elif x < 50:
        return 4
    elif x < 60:
        return 5
    elif x < 70:
        return 6
    else:
        return 7    

train['Age_cat_2'] = train['Age'].apply(category_age)
