# CUSTOM DATASET CLASS
class PlantDataset(Dataset):
    def __init__(
        self, df:pd.DataFrame, imfolder:str, train:bool=True, transforms=None
    ):
        self.df = df
        self.imfolder = imfolder
        self.train = train
        self.transforms = transforms

    def __len__(self):
        return len(self.df)

    def __getitem__(self, index):
        im_path = os.path.join(self.imfolder, self.df.iloc[index]['filename'])
        im = cv2.imread(im_path, cv2.IMREAD_COLOR)
        im = cv2.cvtColor(im, cv2.COLOR_BGR2RGB)

        if (self.transforms):
            '''
            When AlbumentationCompose, a dictionary with key 'image' is created
            '''
            im = self.transforms(image=im)['image']

        if (self.train):
            label = self.df.iloc[index]['pollen_carrying']    #pollen_carrying
            return im, label
        else:
            return im
