def should_hit(player_total, dealer_card_val, player_aces):
    """Return True if the player should hit (request another card) given the current game
    state, or False if the player should stay. player_aces is the number of aces the player has.
    """
    if player_total<12:
        return True
    if player_total>17:
        return False
    if player_total==12 and player_aces==2:
        return True
    if player_total==13 and player_aces==1 and dealer_card_val==5:
        return False
    if player_total==13 and player_aces in (1,3) and dealer_card_val<7:
        return True
    if player_total==14 and player_aces==1 and dealer_card_val==2:
        return True
    if player_total==15 and dealer_card_val==10 and (player_aces in (0,4) or player_aces>5):
        return False
    if player_total>=12 and player_total<=15 and dealer_card_val>6:
        return True
    if player_total==16:
        if player_aces in (0,5):
            if dealer_card_val in (7, 11):
                return True
            else:
                return False
        if player_aces==4 and dealer_card_val==10:
            return False
        if player_aces<7 and dealer_card_val>6:
            return True
        if player_aces>6 and dealer_card_val in (7, 11):
            return True       
    if player_total==17 and player_aces==0 and dealer_card_val==11:
        return True
    if player_total==17 and player_aces>0:
        if player_aces in (4,5) and dealer_card_val==9:
            return False
        if player_aces>1 and player_aces<6 and dealer_card_val==10:
            return False
        if dealer_card_val>6 and player_aces<6:
            return True
        if dealer_card_val==11 and player_aces>5:
            return True
        if dealer_card_val in (7,8) and player_aces==7:
            return True
    return False
