def get_geo(country, province): 
    if province == 'Hubei':
        return 'China/Hubei'
    elif country == 'Mainland China':
        return 'China/Others'
    else:
        return 'Others'

all_stat['geo'] = all_stat.apply(lambda r: get_geo(r.country, r.province), axis=1)

