price_category=[]
for i in udemy_courses['price']:
    if i==0:
        price_category.append('Free')
    elif i>0 and i<=45:
        price_category.append('cheap')
    elif i>45 and i<=95:
        price_category.append('expensive')
    else:
        price_category.append('very expensive')
udemy_courses['price_category']=price_category
