def clip(s, lower_th=1, upper_th=99):
    """
    Clip the value of series
    """
    _lower, _upper = np.percentile(s, [lower_th, upper_th])
    return np.clip(s, _lower, _upper)


def f_normalize(s):
        return (s - np.mean(s)) / np.std(s)

