def categorize_age(x):
    if 18 <= x and x <= 21:
        return '18-21'
    elif 22 <= x and x <= 24:
        return '22-24'
    elif 25 <= x and x <= 29:
        return '25-29'
    elif 30 <= x and x <= 34:
        return '30-34'
    elif 35 <= x and x <= 39:
        return '35-39'
    elif 40 <= x and x <= 44:
        return '40-44'
    elif 45 <= x and x <= 49:
        return '45-49'
    elif 50 <= x and x <= 54:
        return '50-54'
    elif 55 <= x and x <= 59:
        return '55-59'
    elif 60 <= x and x <= 69:
        return '60-69'
    elif 70 <= x and x <= 79:
        return '70-79'
    elif x >= 80:
        return '80+'
    else:
        return 'None'
