import random

def copy_opponent_agent (observation, configuration):
    # random choose of the strategy
    strategy = random.randint(0, 1) 
    if strategy == 0:        
        if observation.step > 0:
            opp_hand = observation.lastOpponentAction
            return (opp_hand + 1) % 3
        else:
            return random.randint(0, 2) 
    else:
        return random.randint(0, 2)
