class RNN():
    def __init__(self, weights):
        self.weights = weights
        self.hidden = None
    def forward(self, x):
        ret = x.copy()
        if self.hidden is not None:
            ret = self.hidden.dot(self.weights) * ret
        self.hidden = ret / ret.sum()
        return ret
    def reset(self):
        self.hidden = None

