def voteSimplifier(v,o):
    d = {}
    for type in v:
        type = str(type).split(';')
        for i in type: 
            if i in d:
                d[i] = d[i] + 1
            else:
                d[i] = 0
    if o == 'v':
        Y = list(d.values())
        X = list(d.keys())
    else: 
        Y = list(d.keys())
        X = list(d.values())
    trace = [go.Bar(
                y=Y,
                x=X,
                orientation = o,
                marker=dict(color=color_brewer),
    )]
    layout = go.Layout(
        margin = go.Margin(
            l = 600 if o == 'h' else 50
        )
    )

    fig = go.Figure(data=trace, layout=layout)
    iplot(fig, filename='horizontal-bar')

