def removeStopWords(lowerArg):
    i=0
    removed=[]
    for x in lowerArg:
        i+=1
        removed.append((' '.join([word for word in x.split() if word not in nltk.corpus.stopwords.words('english')])))

    return pd.Series(removed).astype(str)

