nlp            = load("en_core_web_sm")  
POSs    = {}
STOPs   = {} 
DEPs    = set()
for text in train_data.excerpt:
    doc = nlp(text)
    for token in doc:
        if not token.pos_ in POSs:
            POSs[token.pos_] = set()
        POSs[token.pos_].add(token.tag_) 
        if token.is_stop:
            lower = token.lower_
            STOPs[lower] = (token.pos_,token.tag_)
        DEPs.add(token.dep_)

