def PreProcess(i):
    if(isinstance(i,str)):
        if(i[-1]=='M'):
            return(float(i.lstrip('€').rstrip('M'))*1000000)
        elif(i[-1]=='K'):
            return(float(i.lstrip('€').rstrip('K'))*1000)
        else:
            return(float(i.replace('€','')))

for col in ['Wage', 'Value', 'Release Clause']:
    fifadata[col] = fifadata[col].apply(lambda x: PreProcess(x))

