kf = model_selection.KFold(n_splits=5, shuffle=True, random_state=92)

def kf_split(df, name):
    for fold,(train_indices, valid_indices) in enumerate(kf.split(X=df)):
        df.loc[valid_indices, "kfold"] = fold
    df.to_feather(name)
