from nltk.corpus import stopwords
stop_words=set(stopwords.words("english"))

def remove_stopwords(text):
    message = []

    for word in text:
        if word not in stop_words:
            message.append(word)

    return message

data['text_clean'] = data['text_clean'].apply(lambda x: remove_stopwords(x))

