def bin_count(x):
    if x <= 5:
        return 5
    if x > 5 and x <= 10:
        return 10
    if x > 10 and x <= 15:
        return 15
    if x > 15 and x <= 20:
        return 20
    if x > 20 and x <= 25:
        return 25
    return 30

train["binary_5_10_15_20_25_30"] = train["binary_count"].apply(lambda x: bin_count(x))

