import keras

class myCallBack(keras.callbacks.Callback):
    def on_epoch_end(self, epoch, logs={}):
        acc = logs.get('accuracy')
        if acc >= 1.0:
            print("\nRequired accuracy achieved so ending the training.")
            self.model.stop_training=True

call_back = myCallBack()

