def normality_tests(col):
    _, shapiro = ss.shapiro(col)
    _, normaltest = ss.normaltest(col)
    _, skewtest = ss.skewtest(col)

    return pd.Series(dict(shapiro = shapiro, normaltest = normaltest, skewtest = skewtest))
