%%time

def f1_metric(y_true, y_pred, sample_weight, **kwargs):
    mask = (sample_weight > 1)
    return f1_score(y_true[mask], (y_pred[mask] > 0.5).astype(int), **kwargs)

task = Task('binary', metric = f1_metric, greater_is_better = True)
