df['Price'] = df['Price'].astype('float32')
df['Reviews'] = df['Reviews'].astype('int32')

def size_s2i(s: str, default_val: int) -> int:
    x: int
    if s[-1] == 'k':
        x = int(float(s[:-1]) * 1_000)
    elif s[-1] == 'M':
        x = int(float(s[:-1]) * 1_000_000)
    else:
        x = default_val
    return x

df['Size'] = df['Size'].apply(lambda s: size_s2i(s, 9_400_000))


def installs_s2i(s: str) -> int:
    s = ''.join(s[:-1].split(','))
    return int(s)

df['Installs'] = df['Installs'].apply(installs_s2i).astype('int32')

def version_s2i(s: str) -> int:
    if s[0].isalpha:
        return 100
    s = s.split('.')
    return int(s[0]) * 100 + int(s[1]) * 10 + int(s[2]);

df['Current Ver'] = df['Current Ver'].apply(version_s2i).astype('int32')
df['Android Ver'] = df['Android Ver'].apply(version_s2i).astype('int32')

df['Last Updated'] = pd.to_datetime(df['Last Updated']).astype('datetime64[D]')

