def initialize_parameters():

    w1 = tf.get_variable("w1", [3,3,1,8],initializer = tf.contrib.layers.xavier_initializer(seed = 0))
    w2 = tf.get_variable("w2", [3,3,8,16],initializer = tf.contrib.layers.xavier_initializer(seed = 0))
    parameters = {"W1": w1,
                  "W2": w2}
    return parameters
