speed = int(input("Enter Your Speed: "))
birthVar = str(input("Are you birthday today? please enter: y / n :"))
if birthVar == 'y':
    if speed < 65 :
        print("No Ticket")
    elif (speed > 65) and (speed < 88):
        print("Small Ticket")
    elif speed > 85:
        print("Big Ticket")
elif birthVar == 'n':
    if speed < 60 :
        print("No Ticket")
    elif (speed > 60) and (speed < 80):
        print("Small Ticket")
    elif speed > 80:
        print("Big Ticket")
