def comma2dot(dataframe,feature):

    dirty_data = [i for i in dataframe[feature]]
    clean_data = []
    for i in dirty_data:

        if "," in i:
            i = i.replace(",",".")

        clean_data.append(i)

    dataframe[feature] = clean_data    
    return dataframe

