class ModelA_2: 

    def __init__(self, gas):  # __init__の引数にgasを追加
        self.gas = gas # gasの値が self.gasの初期値になる

    def run(self): 
        self.gas = self.gas - 5 
        print("10km走りました")

    def charge(self, supply_amount): 
        self.gas = self.gas + supply_amount 
        print("現在のガソリンは{}リットルです".format(self.gas))
