def null_table(dataset):

    '''Create table with ammount of null values for dataset'''

    return pd.DataFrame({'Null values': dataset.isnull().sum(), 
                         '% of nulls': round((dataset.isnull().sum() / dataset.shape[0]) * 100, 2)}).T

