import math

def pocetDomuNaUzemi(velikost_domu, velikost_uzemi):
    rada_domu = math.floor(velikost_uzemi / velikost_domu)

    if rada_domu == 0:
        return "Tady se ti nevejde ani jeden dům"
    elif rada_domu % 2 == 0:
        return "Počet domů, co na tomhle území můžeš postavit: " + str(math.floor(velikost_uzemi / velikost_domu)**2) 
    else:
        return "Tady se stavět nehodí, lichý počet domů v řadě nosí neštěstí"

print(pocetDomuNaUzemi(5,12))
print(pocetDomuNaUzemi(6,18))
print(pocetDomuNaUzemi(4,3))
