def filter_unigrams(unigrams, min_count):
    """remove tokens that dont occur at least `min_count` times"""
    tokens_to_filter = [tok for tok, count in unigrams.items() if count < min_count]
    for tok in tokens_to_filter:
        del unigrams[tok]
    return unigrams
