    for i, column in enumerate(column_list):
        color = 'grey'
        if column == 'loan_status':
            color = 'green'
        try:
            cprint("[{}]:{}={} [{}% Null]".format(i,column, description(column), (train_df[column].isna().sum()/len(train_df))*100), color)
