def draw_hist(dataset, rows, cols):
    fig, axes=plt.subplots(nrows=rows, ncols=cols, figsize=(15,12))
    names=dataset.columns.values
    counter=0
    for i in range(rows):
        for j in range(cols):
            if counter>=len(names):
                break
            name=names[counter]
            sns.distplot(a=dataset[name], ax=axes[i,j])

            counter+=1
