def graphLegendFormatting(trace):
    tokens=(trace.name).split(",")
    region=tokens[-1].replace(")","").replace("'","")
    if(len(tokens)>2):
        quantity=tokens[0].replace("(","").replace("'","")
        trace.name="({}.) {}".format(quantity[:3],region)
    else:
        trace.name=region
    return trace
