#Correcting the positions
def position_groups(series):
    if series == 'SS':
        return 'CF'
    elif series == 'LM':
        return 'LW'
    elif series == 'RM':
        return 'RW'
    else:
        return series

dataset['position'] = dataset['position'].apply(position_groups)
