def filter_df(df: pd.DataFrame, col: str) -> pd.DataFrame:
    """Filter the needed columns and binarize them."""
    df = df.copy()
    sub_df = df.filter(regex=f"{col}_Part")
    return sub_df.applymap(lambda x: 1 if x is not np.nan else 0)
