import plotly.express as px
import plotly.graph_objects as go

def plot_import_export_values_over_year(df):
    import_ = {}
    export_ = {}
    for year in df['Year'].unique():
        export_[year] = 0 
        import_[year] = 0
    for year, value, element in zip(df['Year'], df['Value'], df['Element']):
        if element == 'Export Value':
            export_[year] += value
        elif element == 'Import Value':
            import_[year] += value
    fig = go.Figure()
    fig.add_trace(
        go.Scatter(
            x=list(export_.keys()),
            y=list(export_.values()),
            marker_color='lightsalmon',
            name='Export'
        )
    )
    fig.add_trace(
        go.Scatter(
            x=list(import_.keys()),
            y=list(import_.values()),
            marker_color='coral',
            name='Import'
        )
    )
    fig.update_layout({
        'plot_bgcolor': 'rgba(0, 0, 0, 0)',
        'paper_bgcolor': 'rgba(0, 0, 0, 0)',
        'title': 'Real news Frequency words'
    })

    fig.show()



